server/ai-model-extractor.ts.content:
import OpenAI from "openai";

if (!process.env.OPENAI_API_KEY) {
  throw new Error("OPENAI_API_KEY must be set for model number extraction");
}

const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });

export async function extractModelNumberWithAI(productName: string): Promise<string> {
  if (!productName) return 'N/A';

  // First try pattern-based extraction for common formats
  const patterns = [
    // Model after dash: "Product Name - MODEL123"
    /\s-\s([A-Z]{1,}[A-Z0-9]{2,}[A-Z0-9-]*)/i,
    // Model in parentheses: "(MODEL123)" or "(94065325i)"
    /\(([A-Z0-9]{3,}[A-Z0-9-]*)\)/i,
    // Model at start after brand: "Brand MODEL123 description"
    /^[A-Za-z\s]+\s([A-Z]{1,}[A-Z0-9]{2,}[A-Z0-9-]*)\s/i,
    // Alphanumeric codes: "AE12000E", "SP61086", "GENIUS2X4"
    /\b([A-Z]{2,}[0-9]{3,}[A-Z0-9]*)\b/i,
    // With hyphen: "SPi-Pro25", "MA-61224"
    /\b([A-Z]{2,}[A-Z0-9]*-[A-Z0-9]+)\b/i,
  ];

  for (const pattern of patterns) {
    const match = productName.match(pattern);
    if (match && match[1]) {
      const model = match[1].trim();
      // Skip if it's a common word or brand
      const skipWords = ['Heavy', 'Duty', 'Standard', 'Premium', 'Digital', 'Smart', 'Multi', 'Battery', 'Charger'];
      if (!skipWords.includes(model)) {
        return model;
      }
    }
  }

  // If no pattern matches, use AI extraction
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4o-mini", // Cheaper model for simple extraction tasks
      messages: [
        {
          role: "system",
          content: `You are an expert at extracting model numbers from product names. Extract ONLY the model number/part number from the product name, excluding brand names. 

Rules:
- Return only the model number (e.g., "SP61086", "MA61224", "GENIUS2X4", "SPi-Pro25", "KP1460")
- Look for patterns after dashes like "- KP1460"
- Look for alphanumeric codes in parentheses like "(940261345)"
- Do NOT include brand names (Schumacher, Matson, NOCO, SP Tools, Kincrome, etc.)
- Do NOT include descriptive text, voltages, or specifications
- If no clear model number exists, return "N/A"
- Remove parentheses and brackets from model numbers
- Keep alphanumeric characters, hyphens, and underscores only

Examples:
- "Kincrome Battery Load Tester 6&12V - KP1460" â†’ "KP1460"
- "Schumacher SPi Pro25 (94065325i) 12V-25A Battery Charger" â†’ "SPi-Pro25"
- "Matson AE12000E 12V Battery Charger" â†’ "AE12000E"
- "SP Tools SP61086 6, 12 & 24V 26A Smart Battery Charger" â†’ "SP61086"
- "NOCO GENIUS2X4 6V/12V 8A Battery Charger" â†’ "GENIUS2X4"`
        },
        {
          role: "user",
          content: `Extract the model number from: "${productName}"`
        }
      ],
      temperature: 0.1,
      max_tokens: 50
    });

    const extractedModel = response.choices[0].message.content?.trim() || 'N/A';

    // Basic validation - ensure it's not a brand name
    const brandNames = ['schumacher', 'matson', 'noco', 'sp tools', 'sydney tools', 'dewalt', 'makita'];
    const lowerModel = extractedModel.toLowerCase();

    if (brandNames.some(brand => lowerModel.includes(brand))) {
      return 'N/A';
    }

    return extractedModel;

  } catch (error) {
    console.error('Error extracting model number with AI:', error);
    return 'N/A';
  }
}

export async function bulkExtractModelNumbers(products: Array<{id: string, name: string}>): Promise<Array<{id: string, modelNumber: string}>> {
  const results = [];

  // Process in batches to avoid rate limits
  const batchSize = 5;
  for (let i = 0; i < products.length; i += batchSize) {
    const batch = products.slice(i, i + batchSize);
    const batchPromises = batch.map(async (product) => ({
      id: product.id,
      modelNumber: await extractModelNumberWithAI(product.name)
    }));

    const batchResults = await Promise.all(batchPromises);
    results.push(...batchResults);

    // Small delay between batches
    if (i + batchSize < products.length) {
      await new Promise(resolve => setTimeout(resolve, 200));
    }
  }

  return results;
}

server/ai-service.ts.content:
import OpenAI from "openai";

// the newest OpenAI model is "gpt-4o" which was released May 13, 2024. do not change this unless explicitly requested by the user
const openai = new OpenAI({ apiKey: process.env.OPENAI_API_KEY });

interface ProductAnalysis {
  brand: string;
  model: string;
  category: string;
  subcategory: string;
  specifications: string[];
  matchConfidence: number;
}

interface ProductMatch {
  isSameProduct: boolean;
  confidence: number;
  reasoning: string;
}

// Analyze a product title to extract brand, category, and specifications
export async function analyzeProductTitle(title: string): Promise<ProductAnalysis> {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: `You are a product data analyst specializing in tools and industrial equipment. 
          Analyze product titles and extract structured information.
          Respond with JSON in this format: 
          { 
            "brand": "brand name", 
            "model": "model number/name",
            "category": "main category",
            "subcategory": "specific type",
            "specifications": ["spec1", "spec2"],
            "matchConfidence": 0.95
          }`
        },
        {
          role: "user",
          content: `Analyze this product title: "${title}"`
        }
      ],
      response_format: { type: "json_object" },
      temperature: 0.3
    });

    const result = JSON.parse(response.choices[0].message.content || "{}");
    return {
      brand: result.brand || "Unknown",
      model: result.model || "",
      category: result.category || "General",
      subcategory: result.subcategory || "",
      specifications: result.specifications || [],
      matchConfidence: result.matchConfidence || 0.5
    };
  } catch (error) {
    console.error("Error analyzing product:", error);
    // Fallback to basic extraction
    const brandMatch = title.match(/^(\w+)/);
    return {
      brand: brandMatch ? brandMatch[1] : "Unknown",
      model: "",
      category: "General",
      subcategory: "",
      specifications: [],
      matchConfidence: 0.3
    };
  }
}

// Check if two products from different sources are the same
export async function matchProducts(
  product1: { title: string; price: number },
  product2: { title: string; price: number }
): Promise<ProductMatch> {
  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: `You are a product matching expert. Compare two product listings and determine if they are the same product.
          Consider brand, model numbers, specifications, and price similarity.
          Respond with JSON: { "isSameProduct": boolean, "confidence": 0-1, "reasoning": "brief explanation" }`
        },
        {
          role: "user",
          content: `Product 1: "${product1.title}" - $${product1.price}
Product 2: "${product2.title}" - $${product2.price}

Are these the same product?`
        }
      ],
      response_format: { type: "json_object" },
      temperature: 0.2
    });

    const result = JSON.parse(response.choices[0].message.content || "{}");
    return {
      isSameProduct: result.isSameProduct || false,
      confidence: result.confidence || 0,
      reasoning: result.reasoning || "Unable to determine"
    };
  } catch (error) {
    console.error("Error matching products:", error);
    return {
      isSameProduct: false,
      confidence: 0,
      reasoning: "Error during matching"
    };
  }
}

// Extract and normalize category structure from URL
export async function analyzeCategoryUrl(url: string): Promise<{
  category: string;
  subcategory: string;
  expectedBrands: string[];
}> {
  const urlParts = url.split('/');
  const categoryPath = urlParts.slice(urlParts.indexOf('category') + 1).join(' > ');

  try {
    const response = await openai.chat.completions.create({
      model: "gpt-4o",
      messages: [
        {
          role: "system",
          content: `Analyze this category URL path and suggest the proper category structure and expected brands.
          Respond with JSON: { "category": "main", "subcategory": "specific", "expectedBrands": ["brand1", "brand2"] }`
        },
        {
          role: "user",
          content: `Category path: ${categoryPath}\nURL: ${url}`
        }
      ],
      response_format: { type: "json_object" },
      temperature: 0.3
    });

    const result = JSON.parse(response.choices[0].message.content || "{}");
    return {
      category: result.category || "Tools",
      subcategory: result.subcategory || "",
      expectedBrands: result.expectedBrands || []
    };
  } catch (error) {
    console.error("Error analyzing category:", error);
    return {
      category: "Tools",
      subcategory: categoryPath,
      expectedBrands: []
    };
  }
}

// Bulk analyze multiple products for batch import
export async function bulkAnalyzeProducts(
  products: Array<{ title: string; price: number; url: string }>
): Promise<Array<ProductAnalysis & { originalTitle: string; price: number; url: string }>> {
  const analyses = await Promise.all(
    products.map(async (product) => {
      const analysis = await analyzeProductTitle(product.title);
      return {
        ...analysis,
        originalTitle: product.title,
        price: product.price,
        url: product.url
      };
    })
  );

  return analyses;
}

server/competitor-scraper.ts.content:
import axios from 'axios';
import * as cheerio from 'cheerio';
import { extractModelNumberWithAI } from './ai-model-extractor';

export interface CompetitorProduct {
  title: string;
  price: number;
  regularPrice?: number;
  salePrice?: number;
  image?: string;
  url: string;
  brand?: string;
  model?: string;
  category?: string;
  sku?: string;
  competitorName: string;
}

export interface ScrapingResult {
  products: CompetitorProduct[];
  totalProducts: number;
  categoryName: string;
  competitorName: string;
  sourceUrl: string;
  extractedAt: string;
}

export class CompetitorScraper {
  private userAgent = 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/121.0.0.0 Safari/537.36';

  private extractCompetitorName(url: string): string {
    try {
      const hostname = new URL(url).hostname.replace('www.', '');
      const parts = hostname.split('.');
      return parts[0].charAt(0).toUpperCase() + parts[0].slice(1);
    } catch {
      return 'Unknown Competitor';
    }
  }

  private extractCategoryFromUrl(url: string): string {
    const patterns = [
      /\/([^\/]+)\/battery-chargers/i,
      /\/category\/([^\/]+)/i,
      /\/([^\/]+)\/[^\/]*$/i
    ];

    for (const pattern of patterns) {
      const match = url.match(pattern);
      if (match) {
        return match[1].replace(/-/g, ' ').replace(/_/g, ' ')
          .split(' ')
          .map(word => word.charAt(0).toUpperCase() + word.slice(1).toLowerCase())
          .join(' ');
      }
    }
    return 'Products';
  }

  private extractBrand(title: string): string {
    const brandPatterns = [
      /^(SP Tools|Schumacher|Matson|NOCO|DeWalt|Makita|Milwaukee|Bosch|Ryobi|Ozito|SCA|Century|Arlec)/i,
      /^([A-Z][A-Z0-9]+(?:\s+[A-Z][a-z]+)?)/,
      /^([A-Z][a-z]+)/
    ];

    for (const pattern of brandPatterns) {
      const match = title.match(pattern);
      if (match) {
        return match[1].trim();
      }
    }
    return 'Unknown';
  }

  private extractModel(title: string, brand: string): string {
    // Extract model number patterns like SP61084, SP61093, etc.
    const modelPatterns = [
      /([A-Z]{2}\d{5})/i,  // SP61084 format
      /([A-Z]+\d{3,})/i,   // General alphanumeric model
      /(\b\d{5,}\b)/,      // 5+ digit numbers
      /(Model\s+[A-Z0-9-]+)/i,
      /([A-Z0-9-]{6,})/    // General model pattern
    ];

    for (const pattern of modelPatterns) {
      const match = title.match(pattern);
      if (match) {
        return match[1].toUpperCase();
      }
    }

    // Fallback: clean up title
    return title.replace(brand, '').trim().replace(/^[-\s]+/, '').split(' ')[0] || 'Unknown';
  }

  private parsePrice(priceText: string): number {
    // Remove currency symbols and extra text, keep only numbers, dots, and commas
    const cleanPrice = priceText.replace(/[^\d.,]/g, '');
    const numberPrice = parseFloat(cleanPrice.replace(/,/g, ''));
    return isNaN(numberPrice) ? 0 : numberPrice;
  }

  private extractSalePrice($product: any): { regularPrice: number; salePrice: number } {
    // Look for sale price patterns
    const salePriceSelectors = [
      '.price-was, .was-price, .old-price, .original-price',
      '.price-now, .sale-price, .current-price, .special-price',
      '.price-item--sale, .price--sale, .sale',
      '.price .price-reduced, .reduced-price'
    ];

    let regularPrice = 0;
    let salePrice = 0;

    // Try to find both regular and sale prices
    const wasPrice = $product.find('.price-was, .was-price, .old-price, .original-price').first();
    const nowPrice = $product.find('.price-now, .sale-price, .current-price, .special-price, .price').first();

    if (wasPrice.length && nowPrice.length) {
      // Both prices found - product is on sale
      regularPrice = this.parsePrice(wasPrice.text().trim());
      salePrice = this.parsePrice(nowPrice.text().trim());
    } else {
      // Try general price extraction
      const priceElement = $product.find('.price').first();
      if (priceElement.length) {
        const priceText = priceElement.text().trim();
        const price = this.parsePrice(priceText);

        // Check if this looks like a sale (contains "was" or crossed out text)
        if (priceText.toLowerCase().includes('was') || $product.find('.price del, .price strike, .price .strike').length > 0) {
          // Extract both prices from text
          const priceMatches = priceText.match(/\$[\d,]+\.?\d*/g);
          if (priceMatches && priceMatches.length >= 2) {
            regularPrice = this.parsePrice(priceMatches[1]);
            salePrice = this.parsePrice(priceMatches[0]);
          } else {
            salePrice = price;
          }
        } else {
          // Regular price only
          regularPrice = price;
          salePrice = price;
        }
      }
    }

    // Validate prices
    if (salePrice <= 0) salePrice = regularPrice;
    if (regularPrice <= 0) regularPrice = salePrice;

    return { regularPrice, salePrice };
  }

  private normalizeImageUrl(imageUrl: string, baseUrl: string): string {
    if (!imageUrl) return '';

    if (imageUrl.startsWith('//')) {
      return 'https:' + imageUrl;
    }

    if (imageUrl.startsWith('/')) {
      const base = new URL(baseUrl);
      return `${base.protocol}//${base.hostname}${imageUrl}`;
    }

    return imageUrl;
  }

  async scrapeToolkitDepot(url: string): Promise<ScrapingResult> {
    try {
      console.log(`Scraping Toolkit Depot: ${url}`);

      let allProducts: CompetitorProduct[] = [];
      let currentPage = 1;
      const maxPages = 3; // Limit to 3 pages since we expect ~22 products total

      const competitorName = this.extractCompetitorName(url);
      const categoryName = this.extractCategoryFromUrl(url);

      while (currentPage <= maxPages) {
        const pageUrl = currentPage === 1 ? url : `${url.split('?')[0]}?page=${currentPage}`;
        console.log(`Scraping page ${currentPage}: ${pageUrl}`);

        const response = await axios.get(pageUrl, {
          headers: { 'User-Agent': this.userAgent }
        });

        const $ = cheerio.load(response.data);

        const pageProducts = await this.extractProductsFromPage($, url, competitorName, categoryName, allProducts.length);

        if (pageProducts.length === 0) {
          console.log(`No products found on page ${currentPage}, stopping pagination`);
          break;
        }

        allProducts.push(...pageProducts);
        console.log(`Found ${pageProducts.length} products on page ${currentPage}, total: ${allProducts.length}`);

        // Check if there's a next page - look for rel="next" link or pagination elements
        const hasNextPage = $('link[rel="next"], .pagination .next, .pagination a[rel="next"], .next-page, a[aria-label="Next"]').length > 0;
        if (!hasNextPage) {
          console.log('No next page found, stopping pagination');
          break;
        }

        currentPage++;

        // Add a small delay between requests to be respectful
        await new Promise(resolve => setTimeout(resolve, 500));
      }

      console.log(`Extracted ${allProducts.length} total products from ${competitorName}`);

      return {
        products: allProducts,
        totalProducts: allProducts.length,
        categoryName,
        competitorName: competitorName,
        sourceUrl: url,
        extractedAt: new Date().toISOString()
      };

    } catch (error) {
      console.error(`Error scraping ${url}:`, error);
      return {
        products: [],
        totalProducts: 0,
        categoryName: this.extractCategoryFromUrl(url),
        competitorName: this.extractCompetitorName(url),
        sourceUrl: url,
        extractedAt: new Date().toISOString()
      };
    }
  }

  private async extractProductsFromPage($: any, baseUrl: string, competitorName: string, categoryName: string, startIndex: number): Promise<CompetitorProduct[]> {
    const products: CompetitorProduct[] = [];

    // Toolkit Depot specific selectors
    const productSelectors = [
      '.product-item',
      '.product-card', 
      '.woocommerce-loop-product',
      '.product',
      '.product-wrapper',
      '[class*="product"]'
    ];

    let foundProducts = false;

    for (const selector of productSelectors) {
      const productElements = $(selector);

      if (productElements.length > 0) {
        console.log(`Found ${productElements.length} products with selector: ${selector}`);

        const productArray = productElements.toArray();
        for (let i = 0; i < productArray.length; i++) {
          const element = productArray[i];
          const $product = $(element);

          // Extract title
          const titleSelectors = ['h2 a', 'h3 a', '.product-title a', '.woocommerce-loop-product__title', 'a[title]', '.product-name a'];
          let title = '';

          for (const titleSel of titleSelectors) {
            const titleEl = $product.find(titleSel).first();
            if (titleEl.length) {
              title = titleEl.attr('title') || titleEl.text().trim();
              if (title) break;
            }
          }

          // Extract price using enhanced method
          const priceData = this.extractSalePrice($product);
          const price = priceData.salePrice;

          // Extract image
          const imgSelectors = ['img', '.product-image img', '.wp-post-image'];
          let image = '';

          for (const imgSel of imgSelectors) {
            const imgEl = $product.find(imgSel).first();
            if (imgEl.length) {
              const src = imgEl.attr('src') || imgEl.attr('data-src') || imgEl.attr('data-lazy');
              if (src) {
                image = this.normalizeImageUrl(src, baseUrl);
                break;
              }
            }
          }

          // Extract product URL
          const linkSelectors = ['a', '.product-title a', 'h2 a', 'h3 a'];
          let productUrl = baseUrl;

          for (const linkSel of linkSelectors) {
            const linkEl = $product.find(linkSel).first();
            if (linkEl.length) {
              const href = linkEl.attr('href');
              if (href) {
                productUrl = href.startsWith('/') ? 
                  new URL(href, baseUrl).toString() : href;
                break;
              }
            }
          }

          // Filter for battery chargers and relevant products - made more inclusive
          if (title && (
            title.toLowerCase().includes('charger') ||
            title.toLowerCase().includes('battery') ||
            baseUrl.toLowerCase().includes('charger') ||
            title.toLowerCase().includes('jump starter') ||
            title.toLowerCase().includes('jump-starter') ||
            title.toLowerCase().includes('booster') ||
            title.toLowerCase().includes('power') ||
            // Include Kincrome and other battery-related brands
            (title.toLowerCase().includes('kincrome') && title.toLowerCase().includes('starter'))
          )) {
            const brand = this.extractBrand(title);
            const basicModel = this.extractModel(title, brand);

            // Use OpenAI for better model extraction if available
            let enhancedModel = basicModel;
            try {
              if (process.env.OPENAI_API_KEY) {
                enhancedModel = await extractModelNumberWithAI(title);
                if (enhancedModel === 'N/A' || !enhancedModel) {
                  enhancedModel = basicModel; // Fallback to basic extraction
                }
              }
            } catch (error) {
              console.warn('AI model extraction failed, using basic extraction:', error);
              enhancedModel = basicModel;
            }

            const priceData = this.extractSalePrice($product);
            const finalPrice = priceData.salePrice || price || 0;

            products.push({
              title: title,
              price: finalPrice,
              regularPrice: priceData.regularPrice,
              salePrice: priceData.salePrice,
              image: image,
              url: productUrl,
              brand: brand,
              model: enhancedModel,
              category: categoryName,
              sku: `${competitorName.toUpperCase()}-${String(startIndex + products.length + 1).padStart(3, '0')}`,
              competitorName: competitorName
            });
            foundProducts = true;
          }
        }

        if (foundProducts) break;
      }
    }

    return products;
  }

  async scrapeGenericCompetitor(url: string): Promise<ScrapingResult> {
    try {
      console.log(`Scraping generic competitor: ${url}`);

      const response = await axios.get(url, {
        headers: { 'User-Agent': this.userAgent }
      });

      const $ = cheerio.load(response.data);
      const products: CompetitorProduct[] = [];
      const competitorName = this.extractCompetitorName(url);
      const categoryName = this.extractCategoryFromUrl(url);

      // Generic product selectors that work on most e-commerce sites
      const productSelectors = [
        '.product',
        '.product-item',
        '.product-card',
        '[class*="product"]',
        '.item',
        '[data-product]'
      ];

      for (const selector of productSelectors) {
        const productElements = $(selector);

        if (productElements.length > 5) { // Only proceed if we find a reasonable number
          console.log(`Found ${productElements.length} products with selector: ${selector}`);

          productElements.each((index: number, element: any) => {
            if (products.length >= 50) return false; // Limit to 50 products

            const $product = $(element);

            // Extract title using various selectors
            const title = $product.find('h1, h2, h3, h4, [class*="title"], [class*="name"]')
              .first().text().trim() ||
              $product.find('a').first().attr('title') || '';

            // Extract price
            const priceText = $product.find('[class*="price"], .amount, [data-price]')
              .first().text().trim();
            const price = this.parsePrice(priceText);

            // Extract image
            const imgEl = $product.find('img').first();
            const image = imgEl.length ? 
              this.normalizeImageUrl(imgEl.attr('src') || imgEl.attr('data-src') || '', url) : '';

            // Extract product URL
            const linkEl = $product.find('a').first();
            const productUrl = linkEl.length && linkEl.attr('href') ?
              (linkEl.attr('href')!.startsWith('/') ? 
                new URL(linkEl.attr('href')!, url).toString() : 
                linkEl.attr('href')!) : url;

            if (title && title.length > 3) {
              const brand = this.extractBrand(title);
              const model = this.extractModel(title, brand);

              products.push({
                title: title,
                price: price,
                image: image,
                url: productUrl,
                brand: brand,
                model: model,
                category: categoryName,
                sku: `${competitorName.toUpperCase()}-${String(products.length + 1).padStart(3, '0')}`,
                competitorName: competitorName
              });
            }
          });

          if (products.length > 0) break;
        }
      }

      console.log(`Extracted ${products.length} products from ${competitorName}`);

      return {
        products,
        totalProducts: products.length,
        categoryName,
        competitorName,
        sourceUrl: url,
        extractedAt: new Date().toISOString()
      };

    } catch (error) {
      console.error(`Error scraping ${url}:`, error);
      return {
        products: [],
        totalProducts: 0,
        categoryName: this.extractCategoryFromUrl(url),
        competitorName: this.extractCompetitorName(url),
        sourceUrl: url,
        extractedAt: new Date().toISOString()
      };
    }
  }

  async scrapeCompetitor(url: string): Promise<ScrapingResult> {
    const hostname = new URL(url).hostname.toLowerCase();

    // Route to specific scrapers based on domain
    if (hostname.includes('toolkitdepot')) {
      return this.scrapeToolkitDepot(url);
    }

    // Default to generic scraper for other competitors
    return this.scrapeGenericCompetitor(url);
  }
}

export const competitorScraper = new CompetitorScraper();

server/db.ts.content:
import { drizzle } from "drizzle-orm/node-postgres";
import pg from "pg";
import * as schema from "@shared/schema";

const { Pool } = pg;

let db: ReturnType<typeof drizzle> | null = null;
let pool: Pool | null = null;

export function getDb() {
  if (!db && process.env.DATABASE_URL) {
    pool = new Pool({
      connectionString: process.env.DATABASE_URL,
    });
    db = drizzle(pool, { schema });
  }

  if (!db) {
    throw new Error("Database connection not initialized. Check DATABASE_URL environment variable.");
  }

  return db;
}

export function closeDb() {
  if (pool) {
    pool.end();
    pool = null;
    db = null;
  }
}

server/extract-all-models.ts.content:
import { DrizzleStorage } from './storage.drizzle';
import { extractModelNumberWithAI } from './ai-model-extractor';

// Script to extract model numbers for all products
export async function extractAllModelNumbers() {
  const storage = new DrizzleStorage();

  console.log('Starting model number extraction for all products...');

  // Get all products
  const products = await storage.listCatalogProducts();

  let extracted = 0;
  let skipped = 0;
  let failed = 0;

  for (const product of products) {
    // Skip if already has a good model number
    if (product.modelNumber && 
        product.modelNumber !== 'N/A' && 
        product.modelNumber !== 'Unknown' &&
        product.modelNumber !== '') {
      skipped++;
      console.log(`âœ“ Skipping ${product.name} (already has: ${product.modelNumber})`);
      continue;
    }

    try {
      // Extract model number
      const modelNumber = await extractModelNumberWithAI(product.name);

      if (modelNumber && modelNumber !== 'N/A') {
        // Update the product
        await storage.updateProductModelNumber(product.id, modelNumber);
        extracted++;
        console.log(`âœ… Extracted for ${product.name}: ${modelNumber}`);
      } else {
        console.log(`âš ï¸ No model found for ${product.name}`);
      }

      // Small delay to avoid rate limits
      await new Promise(resolve => setTimeout(resolve, 100));

    } catch (error: any) {
      failed++;
      console.error(`âŒ Error processing ${product.name}: ${error.message}`);
    }
  }

  console.log('\n=== Extraction Complete ===');
  console.log(`âœ… Extracted: ${extracted}`);
  console.log(`â­ï¸ Skipped (already had): ${skipped}`);
  console.log(`âŒ Failed: ${failed}`);
  console.log(`ðŸ“Š Total products: ${products.length}`);

  return { extracted, skipped, failed, total: products.length };
}

// Run if called directly
extractAllModelNumbers()
  .then(results => {
    console.log('Done!', results);
    process.exit(0);
  })
  .catch(error => {
    console.error('Fatal error:', error);
    process.exit(1);
  });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";
import { scheduler } from "./scheduler";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
    log(`âœ… Daily price monitoring scheduler active - runs at 12:00 AM AEST`);
  });
})();

server/objectAcl.ts.content:
import { File } from "@google-cloud/storage";

const ACL_POLICY_METADATA_KEY = "custom:aclPolicy";

// The type of the access group.
export enum ObjectAccessGroupType {}

// The logic user group that can access the object.
export interface ObjectAccessGroup {
  type: ObjectAccessGroupType;
  id: string;
}

export enum ObjectPermission {
  READ = "read",
  WRITE = "write",
}

export interface ObjectAclRule {
  group: ObjectAccessGroup;
  permission: ObjectPermission;
}

// The ACL policy of the object.
export interface ObjectAclPolicy {
  owner: string;
  visibility: "public" | "private";
  aclRules?: Array<ObjectAclRule>;
}

// Check if the requested permission is allowed based on the granted permission.
function isPermissionAllowed(
  requested: ObjectPermission,
  granted: ObjectPermission,
): boolean {
  // Users granted with read or write permissions can read the object.
  if (requested === ObjectPermission.READ) {
    return [ObjectPermission.READ, ObjectPermission.WRITE].includes(granted);
  }

  // Only users granted with write permissions can write the object.
  return granted === ObjectPermission.WRITE;
}

// The base class for all access groups.
abstract class BaseObjectAccessGroup implements ObjectAccessGroup {
  constructor(
    public readonly type: ObjectAccessGroupType,
    public readonly id: string,
  ) {}

  // Check if the user is a member of the group.
  public abstract hasMember(userId: string): Promise<boolean>;
}

function createObjectAccessGroup(
  group: ObjectAccessGroup,
): BaseObjectAccessGroup {
  switch (group.type) {
    default:
      throw new Error(`Unknown access group type: ${group.type}`);
  }
}

// Sets the ACL policy to the object metadata.
export async function setObjectAclPolicy(
  objectFile: File,
  aclPolicy: ObjectAclPolicy,
): Promise<void> {
  const [exists] = await objectFile.exists();
  if (!exists) {
    throw new Error(`Object not found: ${objectFile.name}`);
  }

  await objectFile.setMetadata({
    metadata: {
      [ACL_POLICY_METADATA_KEY]: JSON.stringify(aclPolicy),
    },
  });
}

// Gets the ACL policy from the object metadata.
export async function getObjectAclPolicy(
  objectFile: File,
): Promise<ObjectAclPolicy | null> {
  const [metadata] = await objectFile.getMetadata();
  const aclPolicy = metadata?.metadata?.[ACL_POLICY_METADATA_KEY];
  if (!aclPolicy) {
    return null;
  }
  return JSON.parse(aclPolicy as string);
}

// Checks if the user can access the object.
export async function canAccessObject({
  userId,
  objectFile,
  requestedPermission,
}: {
  userId?: string;
  objectFile: File;
  requestedPermission: ObjectPermission;
}): Promise<boolean> {
  // When this function is called, the acl policy is required.
  const aclPolicy = await getObjectAclPolicy(objectFile);
  if (!aclPolicy) {
    return false;
  }

  // Public objects are always accessible for read.
  if (
    aclPolicy.visibility === "public" &&
    requestedPermission === ObjectPermission.READ
  ) {
    return true;
  }

  // Access control requires the user id.
  if (!userId) {
    return false;
  }

  // The owner of the object can always access it.
  if (aclPolicy.owner === userId) {
    return true;
  }

  // Go through the ACL rules to check if the user has the required permission.
  for (const rule of aclPolicy.aclRules || []) {
    const accessGroup = createObjectAccessGroup(rule.group);
    if (
      (await accessGroup.hasMember(userId)) &&
      isPermissionAllowed(requestedPermission, rule.permission)
    ) {
      return true;
    }
  }

  return false;
}

server/objectStorage.ts.content:
import { Storage, File } from "@google-cloud/storage";
import { Response } from "express";
import { randomUUID } from "crypto";
import {
  ObjectAclPolicy,
  ObjectPermission,
  canAccessObject,
  getObjectAclPolicy,
  setObjectAclPolicy,
} from "./objectAcl";

const REPLIT_SIDECAR_ENDPOINT = "http://127.0.0.1:1106";

// The object storage client is used to interact with the object storage service.
export const objectStorageClient = new Storage({
  credentials: {
    audience: "replit",
    subject_token_type: "access_token",
    token_url: `${REPLIT_SIDECAR_ENDPOINT}/token`,
    type: "external_account",
    credential_source: {
      url: `${REPLIT_SIDECAR_ENDPOINT}/credential`,
      format: {
        type: "json",
        subject_token_field_name: "access_token",
      },
    },
    universe_domain: "googleapis.com",
  },
  projectId: "",
});

export class ObjectNotFoundError extends Error {
  constructor() {
    super("Object not found");
    this.name = "ObjectNotFoundError";
    Object.setPrototypeOf(this, ObjectNotFoundError.prototype);
  }
}

// The object storage service is used to interact with the object storage service.
export class ObjectStorageService {
  constructor() {}

  // Gets the public object search paths.
  getPublicObjectSearchPaths(): Array<string> {
    const pathsStr = process.env.PUBLIC_OBJECT_SEARCH_PATHS || "";
    const paths = Array.from(
      new Set(
        pathsStr
          .split(",")
          .map((path) => path.trim())
          .filter((path) => path.length > 0)
      )
    );
    if (paths.length === 0) {
      throw new Error(
        "PUBLIC_OBJECT_SEARCH_PATHS not set. Create a bucket in 'Object Storage' " +
          "tool and set PUBLIC_OBJECT_SEARCH_PATHS env var (comma-separated paths)."
      );
    }
    return paths;
  }

  // Gets the private object directory.
  getPrivateObjectDir(): string {
    const dir = process.env.PRIVATE_OBJECT_DIR || "";
    if (!dir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }
    return dir;
  }

  // Search for a public object from the search paths.
  async searchPublicObject(filePath: string): Promise<File | null> {
    for (const searchPath of this.getPublicObjectSearchPaths()) {
      const fullPath = `${searchPath}/${filePath}`;

      // Full path format: /<bucket_name>/<object_name>
      const { bucketName, objectName } = parseObjectPath(fullPath);
      const bucket = objectStorageClient.bucket(bucketName);
      const file = bucket.file(objectName);

      // Check if file exists
      const [exists] = await file.exists();
      if (exists) {
        return file;
      }
    }

    return null;
  }

  // Downloads an object to the response.
  async downloadObject(file: File, res: Response, cacheTtlSec: number = 3600) {
    try {
      // Get file metadata
      const [metadata] = await file.getMetadata();
      // Get the ACL policy for the object.
      const aclPolicy = await getObjectAclPolicy(file);
      const isPublic = aclPolicy?.visibility === "public";
      // Set appropriate headers
      res.set({
        "Content-Type": metadata.contentType || "application/octet-stream",
        "Content-Length": metadata.size,
        "Cache-Control": `${
          isPublic ? "public" : "private"
        }, max-age=${cacheTtlSec}`,
      });

      // Stream the file to the response
      const stream = file.createReadStream();

      stream.on("error", (err) => {
        console.error("Stream error:", err);
        if (!res.headersSent) {
          res.status(500).json({ error: "Error streaming file" });
        }
      });

      stream.pipe(res);
    } catch (error) {
      console.error("Error downloading file:", error);
      if (!res.headersSent) {
        res.status(500).json({ error: "Error downloading file" });
      }
    }
  }

  // Gets the upload URL for an object entity.
  async getObjectEntityUploadURL(): Promise<string> {
    const privateObjectDir = this.getPrivateObjectDir();
    if (!privateObjectDir) {
      throw new Error(
        "PRIVATE_OBJECT_DIR not set. Create a bucket in 'Object Storage' " +
          "tool and set PRIVATE_OBJECT_DIR env var."
      );
    }

    const objectId = randomUUID();
    const fullPath = `${privateObjectDir}/uploads/${objectId}`;

    const { bucketName, objectName } = parseObjectPath(fullPath);

    // Sign URL for PUT method with TTL
    return signObjectURL({
      bucketName,
      objectName,
      method: "PUT",
      ttlSec: 900,
    });
  }

  // Gets the object entity file from the object path.
  async getObjectEntityFile(objectPath: string): Promise<File> {
    if (!objectPath.startsWith("/objects/")) {
      throw new ObjectNotFoundError();
    }

    const parts = objectPath.slice(1).split("/");
    if (parts.length < 2) {
      throw new ObjectNotFoundError();
    }

    const entityId = parts.slice(1).join("/");
    let entityDir = this.getPrivateObjectDir();
    if (!entityDir.endsWith("/")) {
      entityDir = `${entityDir}/`;
    }
    const objectEntityPath = `${entityDir}${entityId}`;
    const { bucketName, objectName } = parseObjectPath(objectEntityPath);
    const bucket = objectStorageClient.bucket(bucketName);
    const objectFile = bucket.file(objectName);
    const [exists] = await objectFile.exists();
    if (!exists) {
      throw new ObjectNotFoundError();
    }
    return objectFile;
  }

  normalizeObjectEntityPath(
    rawPath: string,
  ): string {
    if (!rawPath.startsWith("https://storage.googleapis.com/")) {
      return rawPath;
    }

    // Extract the path from the URL by removing query parameters and domain
    const url = new URL(rawPath);
    const rawObjectPath = url.pathname;

    let objectEntityDir = this.getPrivateObjectDir();
    if (!objectEntityDir.endsWith("/")) {
      objectEntityDir = `${objectEntityDir}/`;
    }

    if (!rawObjectPath.startsWith(objectEntityDir)) {
      return rawObjectPath;
    }

    // Extract the entity ID from the path
    const entityId = rawObjectPath.slice(objectEntityDir.length);
    return `/objects/${entityId}`;
  }

  // Tries to set the ACL policy for the object entity and return the normalized path.
  async trySetObjectEntityAclPolicy(
    rawPath: string,
    aclPolicy: ObjectAclPolicy
  ): Promise<string> {
    const normalizedPath = this.normalizeObjectEntityPath(rawPath);
    if (!normalizedPath.startsWith("/")) {
      return normalizedPath;
    }

    const objectFile = await this.getObjectEntityFile(normalizedPath);
    await setObjectAclPolicy(objectFile, aclPolicy);
    return normalizedPath;
  }

  // Checks if the user can access the object entity.
  async canAccessObjectEntity({
    userId,
    objectFile,
    requestedPermission,
  }: {
    userId?: string;
    objectFile: File;
    requestedPermission?: ObjectPermission;
  }): Promise<boolean> {
    return canAccessObject({
      userId,
      objectFile,
      requestedPermission: requestedPermission ?? ObjectPermission.READ,
    });
  }
}

function parseObjectPath(path: string): {
  bucketName: string;
  objectName: string;
} {
  if (!path.startsWith("/")) {
    path = `/${path}`;
  }
  const pathParts = path.split("/");
  if (pathParts.length < 3) {
    throw new Error("Invalid path: must contain at least a bucket name");
  }

  const bucketName = pathParts[1];
  const objectName = pathParts.slice(2).join("/");

  return {
    bucketName,
    objectName,
  };
}

async function signObjectURL({
  bucketName,
  objectName,
  method,
  ttlSec,
}: {
  bucketName: string;
  objectName: string;
  method: "GET" | "PUT" | "DELETE" | "HEAD";
  ttlSec: number;
}): Promise<string> {
  const request = {
    bucket_name: bucketName,
    object_name: objectName,
    method,
    expires_at: new Date(Date.now() + ttlSec * 1000).toISOString(),
  };
  const response = await fetch(
    `${REPLIT_SIDECAR_ENDPOINT}/object-storage/signed-object-url`,
    {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(request),
    }
  );
  if (!response.ok) {
    throw new Error(
      `Failed to sign object URL, errorcode: ${response.status}, ` +
        `make sure you're running on Replit`
    );
  }

  const { signed_url: signedURL } = await response.json();
  return signedURL;
}

server/playwright-scraper.ts.content:
import { chromium } from "playwright";

async function whichChromium(): Promise<string | undefined> {
  try {
    const { execSync } = await import("node:child_process");
    return execSync("which chromium").toString().trim();
  } catch { return undefined; }
}
const sleep = (ms: number) => new Promise(r => setTimeout(r, ms));

export const playwrightScraper = {
  async scrapeTotalTools(url: string) {
    const execPath = await whichChromium();
    const browser = await chromium.launch({
      headless: true,
      executablePath: execPath,
      args: ["--no-sandbox","--disable-setuid-sandbox","--disable-dev-shm-usage"]
    });

    const ctx = await browser.newContext({
      userAgent:
        "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/115 Safari/537.36",
      viewport: { width: 1366, height: 900 }
    });

    const page = await ctx.newPage();
    await page.goto(url, { waitUntil: "domcontentloaded", timeout: 60000 });

    // Wait for the product list to load
    await page.waitForSelector('.product-item, .product', { timeout: 10000 }).catch(() => {});

    // Scroll down to trigger lazy loading of images
    await page.waitForLoadState("networkidle").catch(() => {});
    for (let i = 0; i < 10; i++) { 
      await page.mouse.wheel(0, 1000); 
      await sleep(300); 
    }

    // Wait a bit more for images to load
    await sleep(2000);

    const products = await page.$$eval('.product-item, .product', (elements) => {
      const items: any[] = [];
      const seen = new Set<string>();

      for (const el of elements) {
        try {
          const card = el as HTMLElement;

          // Extract product URL
          const linkEl = card.querySelector('a.product-item-link, a[href*="/electrical/"], a[href*="/automotive/"]');
          const href = linkEl ? (linkEl as HTMLAnchorElement).href : '';

          if (!href || seen.has(href)) continue;

          // Extract title
          const titleEl = card.querySelector('.product-item-name, .product-name, h3, .product-item-link');
          const title = titleEl?.textContent?.trim() || '';

          if (!title) continue;

          // Extract price
          let price = "";
          const priceEl = card.querySelector('.price, [data-price-type="finalPrice"], .regular-price, .special-price');
          if (priceEl) {
            price = priceEl.textContent?.replace(/\s+/g, " ").trim() || "";
          }

          // Extract image - look for the actual product image
          let image = null;
          const imgEl = card.querySelector('img.product-image-photo, img[alt*="' + title.substring(0, 20) + '"]') as HTMLImageElement;
          if (imgEl) {
            // Get the src attribute
            image = imgEl.src || imgEl.getAttribute('data-src') || imgEl.getAttribute('data-lazy');

            // Check if it's a placeholder
            if (image && image.includes('data:image') && image.includes('iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR4nGP6zwAAAgcBApocMXEAAAAASUVORK5CYII=')) {
              // Try to get a real image URL
              const allImgs = card.querySelectorAll('img');
              for (const img of allImgs) {
                const src = (img as HTMLImageElement).src;
                if (src && !src.includes('data:image') && src.includes('totaltools.com.au/media')) {
                  image = src;
                  break;
                }
              }
            }
          }

          // Extract brand from title
          const brandMatch = title.match(/^([A-Z]+(?:\s+[A-Z]+)?)\s+/);
          const brand = brandMatch ? brandMatch[1] : '';

          // Extract model number
          const modelPatterns = [
            /\b([A-Z]{2,}\d{4,}[A-Z]*)\b/i,
            /\b([A-Z]+\d+[A-Z]*\d*)\b/i,
          ];
          let model = '';
          for (const pattern of modelPatterns) {
            const match = title.match(pattern);
            if (match) {
              model = match[1];
              break;
            }
          }

          items.push({ title, price, url: href, image, brand, model });
          seen.add(href);
        } catch (err) {
          continue;
        }
      }
      return items;
    });

    await browser.close();

    return {
      products: products.map(p => ({
        title: p.title,
        priceRaw: p.price,
        price: Number((p.price||"").replace(/[^0-9.]/g,"")) || 0,
        image: p.image,
        url: p.url,
        brand: p.brand || "",
        model: p.model || "",
        category: "Battery Chargers",
        sku: "",
        competitorName: "Total Tools",
        hasPromotion: false
      })),
      totalProducts: products.length,
      categoryName: "Battery Chargers",
      competitorName: "Total Tools",
      sourceUrl: url,
      extractedAt: new Date().toISOString(),
      scraperUsed: "Playwright"
    };
  },

  async scrapeSydneyTools(url: string) {
    const execPath = await whichChromium();
    const browser = await chromium.launch({
      headless: true,
      executablePath: execPath,
      args: ["--no-sandbox","--disable-setuid-sandbox","--disable-dev-shm-usage"]
    });

    const ctx = await browser.newContext({
      userAgent:
        "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/115 Safari/537.36",
      viewport: { width: 1366, height: 900 }
    });

    const page = await ctx.newPage();
    await page.goto(url, { waitUntil: "domcontentloaded", timeout: 60000 });
    await page.waitForLoadState("networkidle").catch(() => {});
    for (let i = 0; i < 25; i++) { await page.mouse.wheel(0, 2000); await sleep(250); }

    // For Trade Tools, use more generic selectors
    const isTradeTools = url.includes('tradetools');
    const productSelector = isTradeTools 
      ? "a[href*='/p/'], article[class*='product'], div[class*='product-card'], div[class*='ProductCard']"
      : "a[href^='/product/']";

    const products = await page.$$eval(productSelector, (elements) => {
      const items: any[] = [];
      const seen = new Set<string>();

      for (const el of elements) {
        try {
          // Get the product URL - either from an anchor or a data attribute
          let href = "";
          if (el.tagName === 'A') {
            href = (el as HTMLAnchorElement).href;
          } else {
            const linkEl = el.querySelector('a');
            if (linkEl) {
              href = linkEl.href;
            }
          }

          if (!href || seen.has(href)) continue;

          // Use the element as the card container
          const card = el as HTMLElement;

          // Extract title from various possible locations
          let title = "";
          const titleSelectors = [
            '.product-title', '.product-name', '[class*="ProductName"]', 
            'h2', 'h3', 'h4', '.title', '[class*="title"]'
          ];
          for (const selector of titleSelectors) {
            const titleEl = card.querySelector(selector);
            if (titleEl?.textContent) {
              title = titleEl.textContent.trim();
              break;
            }
          }
          if (!title) continue;

          // Extract price
          let price = "";
          const priceSelectors = [
            '.price', '[class*="price"]', '[data-testid*="price"]',
            '.amount', '[class*="Price"]'
          ];
          for (const selector of priceSelectors) {
            const priceEl = card.querySelector(selector);
            if (priceEl?.textContent && /\d/.test(priceEl.textContent)) {
              price = priceEl.textContent.replace(/\s+/g, " ").trim();
              break;
            }
          }

          // Fallback price extraction
          if (!price) {
            const m = (card.textContent || "").match(/\$\s?\d[\d,]*\.?\d{0,2}/);
            price = m ? m[0] : "";
          }

          // Extract image
          const img = card.querySelector("img");
          const image = img?.getAttribute("src")
            || img?.getAttribute("data-src")
            || img?.getAttribute("data-lazy")
            || (img?.getAttribute("srcset")||"").split(",").pop()?.trim().split(" ")[0]
            || null;

          items.push({ title, price, url: href, image });
          seen.add(href);
        } catch (err) {
          // Skip this element if there's an error
          continue;
        }
      }
      return items;
    });

    await browser.close();

    return {
      products: products.map(p => ({
        title: p.title,
        priceRaw: p.price,
        price: Number((p.price||"").replace(/[^0-9.]/g,"")) || 0,
        image: p.image,
        url: p.url,
        brand: "",
        model: "",
        category: "",
        sku: "",
        competitorName: "Sydney Tools",
        promotion: { hasPromotion: false }
      })),
      totalProducts: products.length,
      categoryName: "",
      competitorName: "Sydney Tools",
      sourceUrl: url,
      extractedAt: new Date().toISOString()
    };
  }
};

server/product-matcher.ts.content:
import { storage } from './storage';
import { extractModelNumberWithAI } from './ai-model-extractor';

export interface ProductMatch {
  catalogProductId: string;
  modelNumber: string;
  productName: string;
  brand: string;
  sydneyToolsPrice?: number;
  competitorPrices: Array<{
    competitorName: string;
    price: number;
    url: string;
    lastUpdated: Date;
  }>;
  priceDifference?: number;
  bestPrice?: number;
  worstPrice?: number;
}

export class ProductMatcher {
  // Match products by model number and merge duplicates
  async matchAndMergeProducts(): Promise<{
    merged: number;
    matched: number;
    errors: string[];
  }> {
    const results = {
      merged: 0,
      matched: 0,
      errors: [] as string[]
    };

    try {
      // Get all catalog products
      const products = await storage.listCatalogProducts();

      // Group products by model number
      const modelGroups = new Map<string, any[]>();

      for (const product of products) {
        if (product.modelNumber && 
            product.modelNumber !== 'N/A' && 
            product.modelNumber !== 'Unknown' &&
            product.modelNumber !== '') {

          const existing = modelGroups.get(product.modelNumber) || [];
          existing.push(product);
          modelGroups.set(product.modelNumber, existing);
        }
      }

      // Process groups with duplicates
      for (const [modelNumber, group] of modelGroups) {
        if (group.length > 1) {
          console.log(`Found ${group.length} products with model ${modelNumber}`);

          try {
            // Use the first product as the master
            const masterProduct = group[0];

            // Merge all competitor listings to the master product
            for (let i = 1; i < group.length; i++) {
              const duplicateProduct = group[i];

              // Get competitor listings for the duplicate
              const listings = await storage.listListingsByProduct(duplicateProduct.id);

              // Transfer listings to master product - create new listing
              for (const listing of listings) {
                await storage.updateListing(listing.id, {
                  productId: masterProduct.id
                });
                results.matched++;
              }

              // Note: We can't delete catalog products yet - need to implement this method
              // await storage.deleteCatalogProduct(duplicateProduct.id);
              results.merged++;

              console.log(`Merged ${duplicateProduct.name} into ${masterProduct.name}`);
            }
          } catch (error: any) {
            results.errors.push(`Error merging model ${modelNumber}: ${error.message}`);
          }
        }
      }

      return results;
    } catch (error: any) {
      console.error('Error in matchAndMergeProducts:', error);
      results.errors.push(error.message);
      return results;
    }
  }

  // Get price comparison for all matched products
  async getProductPriceComparison(): Promise<ProductMatch[]> {
    const matches: ProductMatch[] = [];

    try {
      // Get all catalog products
      const products = await storage.listCatalogProducts();

      for (const product of products) {
        // Skip if no model number
        if (!product.modelNumber || product.modelNumber === 'N/A') continue;

        const match: ProductMatch = {
          catalogProductId: product.id,
          modelNumber: product.modelNumber,
          productName: product.name,
          brand: 'Unknown', // We'll get brand name later
          competitorPrices: []
        };

        // Get competitor listings for this product
        const listings = await storage.listListingsByProduct(product.id);

        // Check if this is a Sydney Tools product (no competitor listings)
        if (!listings || listings.length === 0) {
          // This is our product
          match.sydneyToolsPrice = parseFloat(product.price || '0');
        }

        // Get competitor prices
        for (const listing of listings) {
          // Get latest snapshot for this listing
          const snapshots = await storage.getListingHistory(listing.id, 1);
          if (snapshots.length > 0) {
            const latestSnapshot = snapshots[0];
            match.competitorPrices.push({
              competitorName: 'Competitor', // We'll enhance this later
              price: parseFloat(latestSnapshot.price),
              url: listing.url,
              lastUpdated: latestSnapshot.createdAt
            });
          }
        }

        // Calculate price differences
        const allPrices = [
          ...(match.sydneyToolsPrice ? [match.sydneyToolsPrice] : []),
          ...match.competitorPrices.map(cp => cp.price)
        ].filter(p => p > 0);

        if (allPrices.length > 1) {
          match.bestPrice = Math.min(...allPrices);
          match.worstPrice = Math.max(...allPrices);
          match.priceDifference = match.worstPrice - match.bestPrice;
        }

        matches.push(match);
      }

      // Sort by price difference (biggest savings first)
      matches.sort((a, b) => (b.priceDifference || 0) - (a.priceDifference || 0));

      return matches;
    } catch (error) {
      console.error('Error getting price comparison:', error);
      return matches;
    }
  }

  // Enhanced model extraction for existing products
  async enhanceModelNumbers(): Promise<{
    updated: number;
    errors: string[];
  }> {
    const results = {
      updated: 0,
      errors: [] as string[]
    };

    try {
      const products = await storage.listCatalogProducts();

      for (const product of products) {
        // Skip if already has a good model number
        if (product.modelNumber && 
            product.modelNumber !== 'N/A' && 
            product.modelNumber !== 'Unknown' &&
            product.modelNumber !== '') {
          continue;
        }

        try {
          // Use AI to extract model number
          const modelNumber = await extractModelNumberWithAI(product.name);

          if (modelNumber && modelNumber !== 'N/A') {
            // Note: We need to implement updateCatalogProduct method
            // For now, we'll skip the update
            // await storage.updateCatalogProduct(product.id, { modelNumber });
            results.updated++;
            console.log(`Would update model for ${product.name}: ${modelNumber}`);
          }
        } catch (error: any) {
          results.errors.push(`Error updating ${product.name}: ${error.message}`);
        }
      }

      return results;
    } catch (error: any) {
      console.error('Error enhancing model numbers:', error);
      results.errors.push(error.message);
      return results;
    }
  }
}

export const productMatcher = new ProductMatcher();

server/puppeteer-scraper.ts.content:
import puppeteer from 'puppeteer';

interface ScrapedProduct {
  sku: string;
  title: string;
  price: number;
  image: string;
  url: string;
  brand?: string;
  model?: string;
  category?: string;
}

export class PuppeteerScraper {

  async scrapeSydneyTools(url: string): Promise<{
    products: ScrapedProduct[];
    totalPages: number;
    currentPage: number;
    totalProducts: number;
    categoryName: string;
  }> {
    let browser;

    try {
      console.log('Launching Puppeteer browser for:', url);

      browser = await puppeteer.launch({
        headless: true,
        executablePath: '/nix/store/zi4f80l169xlmivz8vja8wlphq74qqk0-chromium-125.0.6422.141/bin/chromium',
        args: [
          '--no-sandbox',
          '--disable-setuid-sandbox',
          '--disable-dev-shm-usage',
          '--disable-accelerated-2d-canvas',
          '--no-first-run',
          '--no-zygote',
          '--single-process',
          '--disable-gpu',
          '--disable-features=VizDisplayCompositor'
        ]
      });

      const page = await browser.newPage();

      // Set viewport and user agent
      await page.setViewport({ width: 1920, height: 1080 });
      await page.setUserAgent('Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/125.0.0.0 Safari/537.36');

      console.log('Navigating to page...');
      await page.goto(url, { 
        waitUntil: 'networkidle2', 
        timeout: 30000 
      });

      console.log('Waiting for product cards to load...');

      // Wait for products to appear - try multiple selectors
      try {
        await page.waitForSelector('.ant-card.ant-card-bordered.product-card, .product-item, [data-testid="product"]', { 
          timeout: 10000 
        });
      } catch (e) {
        console.log('Product cards not found with primary selectors, trying alternative approach...');
        await page.waitForTimeout(5000); // Wait 5 seconds for JS to fully load
      }

      console.log('Extracting product data...');

      // Extract products using JavaScript execution in the browser
      const products = await page.evaluate(() => {
        const extractedProducts = [];

        // Multiple selectors to try
        const selectors = [
          '.ant-card.ant-card-bordered.product-card',
          '.product-item',
          '[data-testid="product"]',
          '.product-card',
          '.product',
          '[class*="product"]'
        ];

        let productElements = [];
        for (const selector of selectors) {
          productElements = Array.from(document.querySelectorAll(selector));
          if (productElements.length > 0) {
            console.log(`Found ${productElements.length} products with selector: ${selector}`);
            break;
          }
        }

        if (productElements.length === 0) {
          // Try to find any elements that might contain product info
          const allDivs = Array.from(document.querySelectorAll('div'));
          productElements = allDivs.filter(div => {
            const text = div.textContent || '';
            return text.includes('$') && (
              text.toLowerCase().includes('charger') ||
              text.toLowerCase().includes('battery') ||
              text.toLowerCase().includes('jump') ||
              text.toLowerCase().includes('starter')
            );
          });
          console.log(`Found ${productElements.length} potential product elements by content`);
        }

        productElements.forEach((element, index) => {
          try {
            // Extract title from various possible locations
            let title = '';

            const titleSelectors = [
              '.ant-card-meta-title',
              '.product-title',
              'h1', 'h2', 'h3', 'h4', 'h5', 'h6',
              '[title]',
              '[data-testid="product-title"]'
            ];

            for (const selector of titleSelectors) {
              const titleEl = element.querySelector(selector);
              if (titleEl) {
                title = titleEl.textContent?.trim() || titleEl.getAttribute('title') || '';
                if (title) break;
              }
            }

            // If no title found with selectors, try to extract from text content
            if (!title) {
              const text = element.textContent || '';
              const lines = text.split('\n').map(line => line.trim()).filter(line => line.length > 10);
              // Look for a line that seems like a product title
              for (const line of lines) {
                if (line.toLowerCase().includes('charger') || 
                    line.toLowerCase().includes('battery') ||
                    line.toLowerCase().includes('jump') ||
                    line.toLowerCase().includes('starter')) {
                  title = line;
                  break;
                }
              }
            }

            if (!title || title.length < 5) return;

            // Extract price
            let price = 0;
            const priceSelectors = [
              '.price',
              '.ant-typography-title',
              '[class*="price"]',
              '.cost',
              '.amount'
            ];

            for (const selector of priceSelectors) {
              const priceEl = element.querySelector(selector);
              if (priceEl) {
                const priceText = priceEl.textContent?.replace(/[^0-9.]/g, '');
                price = parseFloat(priceText || '0');
                if (price > 0) break;
              }
            }

            // If no price found with selectors, extract from text
            if (price === 0) {
              const text = element.textContent || '';
              const priceMatch = text.match(/\$(\d+(?:,\d{3})*(?:\.\d{2})?)/);
              if (priceMatch) {
                price = parseFloat(priceMatch[1].replace(/,/g, ''));
              }
            }

            if (price === 0) return;

            // Extract image
            let image = '';
            const imgEl = element.querySelector('img');
            if (imgEl) {
              image = imgEl.src || imgEl.getAttribute('data-src') || '';
            }

            // Extract product URL
            let productUrl = '';
            const linkEl = element.querySelector('a') || element.closest('a');
            if (linkEl) {
              productUrl = linkEl.href || '';
            }

            // Generate SKU and extract brand
            const brand = title.split(' ')[0];
            const sku = `${brand.toUpperCase().replace(/[^A-Z0-9]/g, '')}-${String(index + 1).padStart(3, '0')}`;

            extractedProducts.push({
              sku,
              title: title.trim(),
              price,
              image: image.startsWith('http') ? image : (image ? `https://sydneytools.com.au${image}` : ''),
              url: productUrl.startsWith('http') ? productUrl : (productUrl ? `https://sydneytools.com.au${productUrl}` : ''),
              brand,
              model: title.replace(brand, '').trim(),
              category: 'Car Battery Chargers'
            });
          } catch (error) {
            console.error('Error extracting product:', error);
          }
        });

        return extractedProducts;
      });

      console.log(`Puppeteer extracted ${products.length} products`);

      // Get category name
      const categoryName = await page.evaluate(() => {
        const selectors = [
          'h1',
          '.page-title',
          '.category-title',
          '.ant-breadcrumb-link:last-child'
        ];

        for (const selector of selectors) {
          const el = document.querySelector(selector);
          if (el && el.textContent) {
            return el.textContent.trim();
          }
        }

        return 'PRODUCTS';
      });

      return {
        products,
        totalPages: Math.ceil(products.length / 20),
        currentPage: 1,
        totalProducts: products.length,
        categoryName: categoryName.toUpperCase()
      };

    } catch (error) {
      console.error('Puppeteer scraping error:', error);
      throw new Error(`Puppeteer scraping failed: ${error.message}`);
    } finally {
      if (browser) {
        await browser.close();
      }
    }
  }
}

export const puppeteerScraper = new PuppeteerScraper();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { getStorage } from "./storage.factory";
import { 
  insertCompetitorSchema, insertPageSchema, insertProductSchema, insertTaskSchema,
  insertBrandSchema, insertCatalogProductSchema, insertCompetitorListingSchema,
  insertListingSnapshotSchema
} from "@shared/schema";
import {
  ObjectStorageService,
  ObjectNotFoundError,
} from "./objectStorage";
import { extractModelNumberWithAI, bulkExtractModelNumbers } from "./ai-model-extractor";

export async function registerRoutes(app: Express): Promise<Server> {
  const storage = getStorage();

  // Meta endpoints
  app.get("/api/meta", async (req, res) => {
    const categories = await storage.getCategories();
    const productTypes = await storage.getProductTypes();
    const competitors = await storage.getCompetitors();

    res.json({ categories, productTypes, competitors });
  });

  // Competitor endpoints
  app.get("/api/competitors", async (req, res) => {
    const competitors = await storage.getCompetitors();
    res.json(competitors);
  });

  app.post("/api/competitors", async (req, res) => {
    try {
      const validatedData = insertCompetitorSchema.parse(req.body);
      const competitor = await storage.createCompetitor(validatedData);
      res.json(competitor);
    } catch (error) {
      res.status(400).json({ error: "Invalid competitor data" });
    }
  });

  app.put("/api/competitors/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const updates = insertCompetitorSchema.partial().parse(req.body);
      const competitor = await storage.updateCompetitor(id, updates);

      if (!competitor) {
        return res.status(404).json({ error: "Competitor not found" });
      }

      res.json(competitor);
    } catch (error) {
      res.status(400).json({ error: "Invalid competitor data" });
    }
  });

  app.delete("/api/competitors/:id", async (req, res) => {
    const { id } = req.params;
    const deleted = await storage.deleteCompetitor(id);

    if (!deleted) {
      return res.status(404).json({ error: "Competitor not found" });
    }

    res.json({ success: true });
  });

  // Pages endpoints
  app.get("/api/pages", async (req, res) => {
    const { competitorId } = req.query;

    if (competitorId) {
      const pages = await storage.getPagesByCompetitor(competitorId as string);
      res.json(pages);
    } else {
      const pages = await storage.getPages();
      res.json(pages);
    }
  });

  app.post("/api/pages", async (req, res) => {
    try {
      const validatedData = insertPageSchema.parse(req.body);
      const page = await storage.createPage(validatedData);
      res.json(page);
    } catch (error) {
      res.status(400).json({ error: "Invalid page data" });
    }
  });

  app.delete("/api/pages/:id", async (req, res) => {
    const { id } = req.params;
    const deleted = await storage.deletePage(id);

    if (!deleted) {
      return res.status(404).json({ error: "Page not found" });
    }

    res.json({ success: true });
  });

  // Products endpoints
  app.get("/api/products", async (req, res) => {
    const { competitorId, productTypeId, brand } = req.query;
    const filters: any = {};

    if (competitorId) filters.competitorId = competitorId as string;
    if (productTypeId) filters.productTypeId = productTypeId as string;
    if (brand) filters.brand = brand as string;

    const products = await storage.getProducts(filters);
    res.json(products);
  });

  app.get("/api/products/:id", async (req, res) => {
    const { id } = req.params;
    const product = await storage.getProduct(id);

    if (!product) {
      return res.status(404).json({ error: "Product not found" });
    }

    const specs = await storage.getProductSpecs(id);
    const priceSnapshots = await storage.getPriceSnapshots(id);

    res.json({ ...product, specs, priceSnapshots });
  });

  app.post("/api/products", async (req, res) => {
    try {
      const validatedData = insertProductSchema.parse(req.body);
      const product = await storage.createProduct(validatedData);
      res.json(product);
    } catch (error) {
      res.status(400).json({ error: "Invalid product data" });
    }
  });

  // Brand coverage matrix
  app.get("/api/brands/matrix", async (req, res) => {
    const { productTypeId } = req.query;

    if (!productTypeId) {
      return res.status(400).json({ error: "productTypeId is required" });
    }

    const matrix = await storage.getBrandCoverageMatrix(productTypeId as string);
    res.json(matrix);
  });

  // Price bands
  app.get("/api/price-bands", async (req, res) => {
    const { productTypeId, brand } = req.query;
    const bands = await storage.getPriceBands(
      productTypeId as string, 
      brand as string
    );
    res.json(bands);
  });

  // Recent changes
  app.get("/api/changes/recent", async (req, res) => {
    const { hours = "24" } = req.query;
    const changes = await storage.getRecentPriceChanges(parseInt(hours as string));
    res.json(changes);
  });

  // KPI metrics
  app.get("/api/kpi", async (req, res) => {
    const metrics = await storage.getKPIMetrics();
    res.json(metrics);
  });

  // Scraping endpoints
  app.post("/api/scrape/run", async (req, res) => {
    try {
      const { pageId, competitorId, productTypeId } = req.body;

      const task = await storage.createTask({
        pageId: pageId || null,
        status: "pending",
        runReason: "manual"
      });

      // In a real implementation, this would trigger the scraping process
      // For now, we'll just return the task
      res.json({ task, message: "Scraping task queued" });
    } catch (error) {
      res.status(400).json({ error: "Failed to queue scraping task" });
    }
  });

  // Manual price monitoring trigger
  app.post("/api/price-monitoring/run", async (req, res) => {
    try {
      const { scheduler } = await import('./scheduler');

      if (scheduler.isCurrentlyRunning()) {
        return res.status(409).json({ 
          error: "Price monitoring is already running",
          message: "Please wait for the current monitoring cycle to complete"
        });
      }

      // Run price monitoring in background
      scheduler.runManualPriceCheck().then(results => {
        console.log(`Manual price check completed with ${results.length} results`);
