import { useQuery } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Button } from "@/components/ui/button";
import { useState } from "react";
import { formatDistanceToNow } from "date-fns";
import { TrendingDown, TrendingUp, Package, Tag, Clock } from "lucide-react";

export default function Changes() {
  const [hoursFilter, setHoursFilter] = useState("24");
  const [typeFilter, setTypeFilter] = useState("all");

  const { data: recentChanges = [], isLoading } = useQuery({
    queryKey: ["/api/changes/recent", hoursFilter],
    queryFn: () => api.getRecentChanges(parseInt(hoursFilter)),
  });

  const { data: meta } = useQuery({
    queryKey: ["/api/meta"],
    queryFn: api.getMeta,
  });

  // Filter changes by type
  const filteredChanges = typeFilter && typeFilter !== "all"
    ? recentChanges.filter((change: any) => change.changeType === typeFilter)
    : recentChanges;

  const getChangeIcon = (changeType: string) => {
    switch (changeType) {
      case 'price_drop':
        return <TrendingDown className="text-red-600" size={16} />;
      case 'price_increase':
        return <TrendingUp className="text-blue-600" size={16} />;
      case 'stock_change':
        return <Package className="text-green-600" size={16} />;
      case 'promo_added':
        return <Tag className="text-yellow-600" size={16} />;
      default:
        return <Clock className="text-slate-600" size={16} />;
    }
  };

  const getChangeColor = (changeType: string) => {
    switch (changeType) {
      case 'price_drop':
        return 'bg-red-500';
      case 'price_increase':
        return 'bg-blue-500';
      case 'stock_change':
        return 'bg-green-500';
      case 'promo_added':
        return 'bg-yellow-500';
      default:
        return 'bg-slate-500';
    }
  };

  const getChangeBadge = (change: any) => {
    switch (change.changeType) {
      case 'price_drop':
        return {
          text: 'Price Drop',
          variant: 'destructive' as const,
          detail: `$${change.oldValue} â†’ $${change.newValue}`
        };
      case 'price_increase':
        return {
          text: 'Price Increase',
          variant: 'default' as const,
          detail: `$${change.oldValue} â†’ $${change.newValue}`
        };
      case 'stock_change':
        return {
          text: change.newValue === 'in_stock' ? 'Back in Stock' : 'Out of Stock',
          variant: 'default' as const,
          detail: ''
        };
      case 'promo_added':
        return {
          text: 'Promo Added',
          variant: 'secondary' as const,
          detail: change.newValue || ''
        };
      default:
        return {
          text: 'Change',
          variant: 'secondary' as const,
          detail: ''
        };
    }
  };

  const getChangeTypeStats = () => {
    const stats = recentChanges.reduce((acc: any, change: any) => {
      acc[change.changeType] = (acc[change.changeType] || 0) + 1;
      return acc;
    }, {});

    return [
      { type: 'price_drop', label: 'Price Drops', count: stats.price_drop || 0, color: 'text-red-600' },
      { type: 'price_increase', label: 'Price Increases', count: stats.price_increase || 0, color: 'text-blue-600' },
      { type: 'stock_change', label: 'Stock Changes', count: stats.stock_change || 0, color: 'text-green-600' },
      { type: 'promo_added', label: 'Promos Added', count: stats.promo_added || 0, color: 'text-yellow-600' },
    ];
  };

  const stats = getChangeTypeStats();

  return (
    <div className="bg-gradient-to-br from-gray-50 via-white to-gray-100">
      <div className="p-8 animate-fade-up">
        {/* Stats Overview */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
          {stats.map((stat) => (
            <Card key={stat.type} className="border-slate-200 shadow-sm">
              <CardContent className="p-6">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-slate-600 text-sm font-medium">{stat.label}</p>
                    <p className={`text-2xl font-bold mt-1 ${stat.color}`}>{stat.count}</p>
                  </div>
                  <div className="w-12 h-12 bg-slate-100 rounded-lg flex items-center justify-center">
                    {getChangeIcon(stat.type)}
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {/* Filters */}
        <Card className="border-slate-200 shadow-sm mb-6">
          <CardHeader className="border-b border-slate-200">
            <div className="flex items-center justify-between">
              <CardTitle className="text-lg font-semibold text-slate-900">
                Change History
              </CardTitle>
              <div className="flex items-center space-x-4">
                <div className="flex items-center space-x-2">
                  <label className="text-sm font-medium text-slate-700">Time Period:</label>
                  <Select value={hoursFilter} onValueChange={setHoursFilter}>
                    <SelectTrigger className="w-32">
                      <SelectValue />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="1">Last Hour</SelectItem>
                      <SelectItem value="6">Last 6 Hours</SelectItem>
                      <SelectItem value="24">Last 24 Hours</SelectItem>
                      <SelectItem value="72">Last 3 Days</SelectItem>
                      <SelectItem value="168">Last Week</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
                <div className="flex items-center space-x-2">
                  <label className="text-sm font-medium text-slate-700">Change Type:</label>
                  <Select value={typeFilter} onValueChange={setTypeFilter}>
                    <SelectTrigger className="w-40">
                      <SelectValue placeholder="All Types" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Types</SelectItem>
                      <SelectItem value="price_drop">Price Drops</SelectItem>
                      <SelectItem value="price_increase">Price Increases</SelectItem>
                      <SelectItem value="stock_change">Stock Changes</SelectItem>
                      <SelectItem value="promo_added">Promos Added</SelectItem>
                    </SelectContent>
                  </Select>
                </div>
              </div>
            </div>
          </CardHeader>
          <CardContent className="p-6">
            {isLoading ? (
              <div className="space-y-4">
                {[...Array(5)].map((_, i) => (
                  <div key={i} className="animate-pulse">
                    <div className="flex items-start space-x-3 p-3 bg-slate-100 rounded-lg">
                      <div className="w-2 h-2 bg-slate-300 rounded-full mt-2"></div>
                      <div className="flex-1 space-y-2">
                        <div className="h-4 bg-slate-300 rounded w-3/4"></div>
                        <div className="h-3 bg-slate-300 rounded w-1/2"></div>
                        <div className="flex space-x-2">
                          <div className="h-6 bg-slate-300 rounded w-20"></div>
                          <div className="h-6 bg-slate-300 rounded w-24"></div>
                        </div>
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            ) : filteredChanges.length > 0 ? (
              <div className="space-y-4 max-h-96 overflow-y-auto">
                {filteredChanges.map((change: any) => {
                  const badge = getChangeBadge(change);
                  return (
                    <div key={change.id} className="flex items-start space-x-3 p-3 bg-slate-50 rounded-lg hover:bg-slate-100 transition-colors">
                      <div className={`flex-shrink-0 w-2 h-2 ${getChangeColor(change.changeType)} rounded-full mt-2`} />
                      <div className="flex-1 min-w-0">
                        <p className="text-sm font-medium text-slate-900">{change.productTitle}</p>
                        <p className="text-sm text-slate-600">{change.competitorName}</p>
                        <div className="flex items-center space-x-2 mt-1">
                          <Badge variant={badge.variant} className="text-xs">
                            {badge.text}
                          </Badge>
                          {badge.detail && (
                            <span className="text-xs font-mono text-slate-500">{badge.detail}</span>
                          )}
                        </div>
                        <p className="text-xs text-slate-400 mt-1">
                          {formatDistanceToNow(new Date(change.timestamp), { addSuffix: true })}
                        </p>
                      </div>
                      <div className="flex-shrink-0">
                        {getChangeIcon(change.changeType)}
                      </div>
                    </div>
                  );
                })}
              </div>
            ) : (
              <div className="text-center py-12">
                <Clock className="mx-auto h-12 w-12 text-slate-400 mb-4" />
                <h3 className="text-lg font-medium text-slate-900 mb-2">
                  {typeFilter ? `No ${typeFilter.replace('_', ' ')} changes` : 'No Recent Changes'}
                </h3>
                <p className="text-slate-500 mb-4">
                  {typeFilter 
                    ? `No ${typeFilter.replace('_', ' ')} found in the selected time period.`
                    : `No changes detected in the last ${hoursFilter} hours.`
                  }
                </p>
                {typeFilter && (
                  <Button variant="outline" onClick={() => setTypeFilter("")}>
                    Show All Changes
                  </Button>
                )}
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/competitors.tsx.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { api } from "@/lib/api";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Switch } from "@/components/ui/switch";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { insertCompetitorSchema } from "@shared/schema";
import { useToast } from "@/hooks/use-toast";
import { useState } from "react";
import { Plus, Globe, Trash2, Edit } from "lucide-react";
import { z } from "zod";

type CompetitorFormData = z.infer<typeof insertCompetitorSchema>;

export default function Competitors() {
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [editingCompetitor, setEditingCompetitor] = useState<any>(null);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: competitors = [] } = useQuery({
    queryKey: ["/api/competitors"],
    queryFn: api.getCompetitors,
  });

  const form = useForm<CompetitorFormData>({
    resolver: zodResolver(insertCompetitorSchema),
    defaultValues: {
      name: "",
      siteDomain: "",
      status: "active",
      isUs: false,
    },
  });

  const createMutation = useMutation({
    mutationFn: api.createCompetitor,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/competitors"] });
      setIsDialogOpen(false);
      form.reset();
      toast({
        title: "Success",
        description: "Competitor created successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to create competitor.",
        variant: "destructive",
      });
    },
  });

  const updateMutation = useMutation({
    mutationFn: ({ id, data }: { id: string; data: Partial<CompetitorFormData> }) =>
      api.updateCompetitor(id, data),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/competitors"] });
      setIsDialogOpen(false);
      setEditingCompetitor(null);
      form.reset();
      toast({
        title: "Success",
        description: "Competitor updated successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to update competitor.",
        variant: "destructive",
      });
    },
  });

  const deleteMutation = useMutation({
    mutationFn: api.deleteCompetitor,
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/competitors"] });
      toast({
        title: "Success",
        description: "Competitor deleted successfully.",
      });
    },
    onError: () => {
      toast({
        title: "Error",
        description: "Failed to delete competitor.",
        variant: "destructive",
      });
    },
  });

  const onSubmit = (data: CompetitorFormData) => {
    if (editingCompetitor) {
      updateMutation.mutate({ id: editingCompetitor.id, data });
    } else {
      createMutation.mutate(data);
    }
  };

  const handleEdit = (competitor: any) => {
    setEditingCompetitor(competitor);
    form.reset(competitor);
    setIsDialogOpen(true);
  };

  const handleDelete = (id: string) => {
    if (confirm("Are you sure you want to delete this competitor?")) {
      deleteMutation.mutate(id);
    }
  };

  const handleDialogClose = () => {
    setIsDialogOpen(false);
    setEditingCompetitor(null);
    form.reset();
  };

  return (
    <div className="bg-gradient-to-br from-gray-50 via-white to-gray-100">
      <div className="p-8 animate-fade-up">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-lg font-semibold text-slate-900">Active Competitors</h2>
          <Dialog open={isDialogOpen} onOpenChange={handleDialogClose}>
            <DialogTrigger asChild>
              <Button>
                <Plus className="mr-2 h-4 w-4" />
                Add Competitor
              </Button>
            </DialogTrigger>
            <DialogContent>
              <DialogHeader>
                <DialogTitle>
                  {editingCompetitor ? "Edit Competitor" : "Add New Competitor"}
                </DialogTitle>
              </DialogHeader>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Name</FormLabel>
                        <FormControl>
                          <Input placeholder="e.g., Bunnings Warehouse" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="siteDomain"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Site Domain</FormLabel>
                        <FormControl>
                          <Input placeholder="e.g., bunnings.com.au" {...field} />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                  <FormField
                    control={form.control}
                    name="isUs"
                    render={({ field }) => (
                      <FormItem className="flex flex-row items-center justify-between rounded-lg border p-3">
                        <div className="space-y-0.5">
                          <FormLabel>This is our company</FormLabel>
                          <div className="text-sm text-slate-500">
                            Mark if this represents your own company
                          </div>
                        </div>
                        <FormControl>
                          <Switch
                            checked={field.value}
                            onCheckedChange={field.onChange}
                          />
                        </FormControl>
                      </FormItem>
                    )}
                  />
                  <div className="flex justify-end space-x-2">
                    <Button type="button" variant="outline" onClick={handleDialogClose}>
                      Cancel
                    </Button>
                    <Button 
                      type="submit" 
                      disabled={createMutation.isPending || updateMutation.isPending}
                    >
                      {editingCompetitor ? "Update" : "Create"}
                    </Button>
                  </div>
                </form>
              </Form>
            </DialogContent>
          </Dialog>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {competitors.map((competitor: any) => (
            <Card key={competitor.id} className="border-slate-200 shadow-sm">
              <CardHeader className="pb-4">
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="w-10 h-10 bg-primary/10 rounded-lg flex items-center justify-center">
                      <Globe className="text-primary" size={18} />
                    </div>
                    <div>
                      <CardTitle className="text-lg font-semibold text-slate-900">
                        {competitor.name}
                      </CardTitle>
                      <p className="text-sm text-slate-500">{competitor.siteDomain}</p>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => handleEdit(competitor)}
                    >
                      <Edit size={14} />
                    </Button>
                    <Button
                      variant="ghost"
                      size="sm"
                      onClick={() => handleDelete(competitor.id)}
                      disabled={deleteMutation.isPending}
                    >
                      <Trash2 size={14} />
                    </Button>
                  </div>
                </div>
              </CardHeader>

              <CardContent>
                <div className="space-y-3">
                  <div className="flex items-center justify-between">
                    <span className="text-sm font-medium text-slate-700">Status</span>
                    <Badge variant={competitor.status === "active" ? "default" : "secondary"}>
                      {competitor.status}
                    </Badge>
                  </div>

                  {competitor.isUs && (
                    <div className="flex items-center justify-between">
                      <span className="text-sm font-medium text-slate-700">Type</span>
                      <Badge variant="outline" className="text-blue-600 border-blue-200">
                        Our Company
                      </Badge>
                    </div>
                  )}

                  <div className="pt-3 border-t border-slate-200">
                    <div className="grid grid-cols-2 gap-4 text-center">
                      <div>
                        <p className="text-lg font-semibold text-slate-900">0</p>
                        <p className="text-xs text-slate-500">Pages</p>
                      </div>
                      <div>
                        <p className="text-lg font-semibold text-slate-900">0</p>
                        <p className="text-xs text-slate-500">Products</p>
                      </div>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>

        {competitors.length === 0 && (
          <Card className="border-slate-200 shadow-sm">
            <CardContent className="py-12 text-center">
              <Globe className="mx-auto h-12 w-12 text-slate-400 mb-4" />
              <h3 className="text-lg font-medium text-slate-900 mb-2">No Competitors</h3>
              <p className="text-slate-500 mb-4">
                Add competitor websites to start tracking their products and pricing.
              </p>
            </CardContent>
          </Card>
        )}
      </div>
    </div>
  );
}

