import { multiSiteScraper } from './multi-site-scraper';
import { playwrightScraper } from './playwright-scraper';

async function testScrapers() {
  console.log('Testing Multi-Site Scraper...\n');

  // Test 1: Standard scraper on static site
  try {
    console.log('Testing Toolkit Depot (standard scraper)...');
    const toolkitResult = await multiSiteScraper.scrapeCompetitor(
      'https://www.toolkitdepot.com.au/car-battery-chargers-jump-starters/car-battery-chargers'
    );
    console.log(`Found ${toolkitResult.products.length} products from Toolkit Depot`);
    if (toolkitResult.products.length > 0) {
      console.log('Sample product:', toolkitResult.products[0]);
    }
  } catch (error) {
    console.error('Toolkit Depot scraping failed:', error);
  }

  // Test 2: Playwright scraper on Sydney Tools
  try {
    console.log('\nTesting Sydney Tools (Playwright scraper)...');
    const sydneyResult = await playwrightScraper.scrapeSydneyTools(
      'https://sydneytools.com.au/collections/battery-chargers'
    );
    console.log(`Found ${sydneyResult.products.length} products from Sydney Tools`);
    if (sydneyResult.products.length > 0) {
      console.log('Sample product:', sydneyResult.products[0]);
    }
  } catch (error) {
    console.error('Sydney Tools scraping failed:', error);
  }

  // Test 3: Generic scraper fallback
  try {
    console.log('\nTesting generic scraper on unknown site...');
    const genericResult = await multiSiteScraper.scrapeGeneric(
      'https://example-tools.com/products'
    );
    console.log(`Found ${genericResult.products.length} products from generic site`);
  } catch (error) {
    console.error('Generic scraping failed:', error);
  }
}

testScrapers().then(() => {
  console.log('\nScraper tests complete');
  process.exit(0);
}).catch(error => {
  console.error('Test failed:', error);
  process.exit(1);
});

server/trade-tools-scraper.ts.content:
import { chromium, Page } from 'playwright';
import { exec } from 'child_process';
import { promisify } from 'util';

const execAsync = promisify(exec);

async function whichChromium() {
  try {
    const { stdout } = await execAsync("which chromium");
    return stdout.trim();
  } catch { 
    return undefined; 
  }
}

const sleep = (ms: number) => new Promise(r => setTimeout(r, ms));

export const tradeToolsScraper = {
  async scrapeTradeTools(url: string) {
    console.log('Starting Trade Tools specific scraper for:', url);

    const execPath = await whichChromium();
    const browser = await chromium.launch({
      headless: true,
      executablePath: execPath,
      args: ["--no-sandbox", "--disable-setuid-sandbox", "--disable-dev-shm-usage"]
    });

    const ctx = await browser.newContext({
      userAgent: "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/120.0.0.0 Safari/537.36",
      viewport: { width: 1920, height: 1080 }
    });

    const page = await ctx.newPage();

    try {
      // Navigate to the page
      await page.goto(url, { waitUntil: "domcontentloaded", timeout: 60000 });
      console.log('Page loaded, waiting for products...');

      // Wait for the page to stabilize
      await page.waitForLoadState("networkidle", { timeout: 30000 }).catch(() => {
        console.log('Network idle timeout - continuing');
      });

      // Wait a bit for React to render
      await sleep(3000);

      // Scroll to trigger lazy loading
      console.log('Scrolling to load all products...');
      const scrollHeight = await page.evaluate(() => document.body.scrollHeight);
      const viewportHeight = await page.evaluate(() => window.innerHeight);
      const scrollSteps = Math.ceil(scrollHeight / viewportHeight);

      for (let i = 0; i < scrollSteps; i++) {
        await page.evaluate((step) => {
          window.scrollTo(0, step * window.innerHeight);
        }, i);
        await sleep(500);
      }

      // Scroll back to top
      await page.evaluate(() => window.scrollTo(0, 0));
      await sleep(1000);

      // Extract products using page evaluation
      console.log('Extracting products...');
      const products = await page.evaluate(() => {
        const items: any[] = [];

        // Strategy 1: Look for any element containing product information
        // Trade Tools uses minified class names, so we need to be smart about detection

        // Find all links that might be products
        const allLinks = Array.from(document.querySelectorAll('a'));
        const productLinks = allLinks.filter(link => {
          const href = link.getAttribute('href') || '';
          // Trade Tools product URLs contain /p/ followed by product code
          return href.includes('/p/') && !href.includes('signin') && !href.includes('cart');
        });

        console.log(`Found ${productLinks.length} potential product links`);

        // If we found product links, extract data from their containers
        if (productLinks.length > 0) {
          productLinks.forEach(link => {
            const href = link.href;
            const container = link.closest('article') || link.parentElement?.parentElement || link.parentElement;

            if (!container) return;

            // Extract title - look for heading tags or the link text
            let title = '';
            const heading = container.querySelector('h1, h2, h3, h4, h5, h6');
            if (heading) {
              title = heading.textContent?.trim() || '';
            } else {
              // Sometimes the title is in the link itself
              const linkText = link.textContent?.trim() || '';
              if (linkText && linkText.length > 10 && !linkText.includes('$')) {
                title = linkText;
              }
            }

            // Extract price - look for dollar signs
            let price = '';
            let originalPrice = '';
            const priceMatches = (container.textContent || '').matchAll(/\$[\d,]+(?:\.\d{2})?/g);
            const prices = Array.from(priceMatches).map(m => m[0]);
            if (prices.length > 0) {
              price = prices[prices.length - 1]; // Usually the last price is the current price
              if (prices.length > 1) {
                originalPrice = prices[0]; // First price might be the original
              }
            }

            // Extract image
            let image = '';
            const img = container.querySelector('img');
            if (img) {
              image = img.src || img.dataset.src || img.dataset.lazySrc || '';
              // Convert relative URLs to absolute
              if (image && !image.startsWith('http')) {
                image = new URL(image, window.location.origin).toString();
              }
            }

            // Extract product code
            let code = '';
            const codeMatch = container.textContent?.match(/Product code:\s*([A-Z0-9-]+)/i);
            if (codeMatch) {
              code = codeMatch[1];
            }

            // Only add if we have at least a title
            if (title) {
              items.push({
                title,
                price,
                originalPrice: originalPrice !== price ? originalPrice : '',
                url: href,
                image,
                code
              });
            }
          });
        }

        // Strategy 2: If no product links found, look for product gallery items
        if (items.length === 0) {
          const gallery = document.querySelector('[class*="productGallery"]');
          if (gallery) {
            // Find all direct children that might be products
            const children = Array.from(gallery.children);
            console.log(`Found gallery with ${children.length} children`);

            children.forEach(child => {
              const link = child.querySelector('a');
              const img = child.querySelector('img');
              const text = child.textContent || '';

              // Extract product info from text
              const titleMatch = text.match(/([A-Z][^$]+?)(?:\$|Product code:)/);
              const priceMatch = text.match(/\$[\d,]+(?:\.\d{2})?/);
              const codeMatch = text.match(/Product code:\s*([A-Z0-9-]+)/i);

              if (titleMatch || priceMatch) {
                items.push({
                  title: titleMatch ? titleMatch[1].trim() : 'Product',
                  price: priceMatch ? priceMatch[0] : '',
                  url: link ? link.href : '',
                  image: img ? (img.src || img.dataset.src || '') : '',
                  code: codeMatch ? codeMatch[1] : ''
                });
              }
            });
          }
        }

        return items;
      });

      console.log(`Extracted ${products.length} products`);

      // Clean up and format the products
      const formattedProducts = products.map(p => {
        // Parse price to remove $ and convert to number
        const parsePrice = (priceStr: string) => {
          if (!priceStr) return null;
          const cleaned = priceStr.replace(/[^0-9.]/g, '');
          const num = parseFloat(cleaned);
          return isNaN(num) ? null : num;
        };

        return {
          title: p.title,
          price: parsePrice(p.price),
          originalPrice: parsePrice(p.originalPrice),
          url: p.url,
          image: p.image,
          modelNumber: p.code,
          inStock: true
        };
      }).filter(p => p.title && p.title.length > 0);

      await browser.close();

      return {
        success: true,
        products: formattedProducts,
        competitorName: 'Trade Tools',
        message: `Found ${formattedProducts.length} products from Trade Tools`
      };

    } catch (error: any) {
      console.error('Error scraping Trade Tools:', error);
      await browser.close();

      return {
        success: false,
        products: [],
        competitorName: 'Trade Tools',
        message: `Failed to scrape Trade Tools: ${error.message}`
      };
    }
  }
};

client/src/components/ImportReviewDialog.content:
import { useState } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogDescription } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Checkbox } from "@/components/ui/checkbox";
import { Card, CardContent } from "@/components/ui/card";
import { 
  AlertCircle, 
  CheckCircle2, 
  Package, 
  Link, 
  X,
  Loader2,
  ShoppingCart,
  DollarSign
} from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useMutation } from "@tanstack/react-query";
import { apiRequest, queryClient } from "@/lib/queryClient";

interface Product {
  title: string;
  price: number;
  image?: string;
  url?: string;
  brand?: string;
  modelNumber?: string;
  isNew: boolean;
  matchedProduct?: {
    id: string;
    name: string;
    modelNumber: string;
  };
}

interface ImportReviewDialogProps {
  isOpen: boolean;
  onClose: () => void;
  previewData: {
    competitorName: string;
    sourceUrl: string;
    totalProducts: number;
    newProducts: number;
    matchedProducts: number;
    products: Product[];
    scraperUsed?: string;
  } | null;
}

export function ImportReviewDialog({ isOpen, onClose, previewData }: ImportReviewDialogProps) {
  const { toast } = useToast();
  const [selectedProducts, setSelectedProducts] = useState<Set<number>>(new Set());
  const [selectAll, setSelectAll] = useState(false);

  // Confirm import mutation
  const confirmImport = useMutation({
    mutationFn: async (data: { products: Product[], competitorName: string, sourceUrl: string }) => {
      const response = await apiRequest("POST", "/api/confirm-import", data);
      return await response.json();
    },
    onSuccess: (data: any) => {
      queryClient.invalidateQueries({ queryKey: ["/api/products-unified"] });
      queryClient.invalidateQueries({ queryKey: ["/api/competitors"] });

      toast({ 
        title: "Import Successful",
        description: data.message || `Successfully imported ${data.savedProducts} products`
      });

      onClose();
      setSelectedProducts(new Set());
    },
    onError: (error: any) => {
      toast({ 
        title: "Import Failed",
        description: error.message || "Failed to import products",
        variant: "destructive"
      });
    }
  });

  if (!previewData) return null;

  const handleSelectAll = () => {
    if (selectAll) {
      setSelectedProducts(new Set());
    } else {
      const allIndices = previewData.products.map((_, index) => index);
      setSelectedProducts(new Set(allIndices));
    }
    setSelectAll(!selectAll);
  };

  const handleSelectProduct = (index: number) => {
    const newSelected = new Set(selectedProducts);
    if (newSelected.has(index)) {
      newSelected.delete(index);
    } else {
      newSelected.add(index);
    }
    setSelectedProducts(newSelected);
    setSelectAll(newSelected.size === previewData.products.length);
  };

  const handleConfirmImport = () => {
    const productsToImport = previewData.products.filter((_, index) => selectedProducts.has(index));

    if (productsToImport.length === 0) {
      toast({ 
        title: "No products selected",
        description: "Please select at least one product to import",
        variant: "destructive"
      });
      return;
    }

    confirmImport.mutate({
      products: productsToImport,
      competitorName: previewData.competitorName,
      sourceUrl: previewData.sourceUrl
    });
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="max-w-5xl max-h-[90vh]">
        <DialogHeader>
          <DialogTitle className="text-xl">Review Import from {previewData.competitorName}</DialogTitle>
          <DialogDescription>
            Found {previewData.totalProducts} products â€¢ {previewData.newProducts} new â€¢ {previewData.matchedProducts} matched
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {/* Stats Bar */}
          <div className="flex gap-4 p-3 bg-slate-50 rounded-lg">
            <div className="flex items-center gap-2">
              <Package className="h-4 w-4 text-slate-600" />
              <span className="text-sm font-medium">{previewData.totalProducts} Total</span>
            </div>
            <div className="flex items-center gap-2">
              <CheckCircle2 className="h-4 w-4 text-green-600" />
              <span className="text-sm font-medium text-green-600">{previewData.newProducts} New</span>
            </div>
            <div className="flex items-center gap-2">
              <Link className="h-4 w-4 text-blue-600" />
              <span className="text-sm font-medium text-blue-600">{previewData.matchedProducts} Matched</span>
            </div>
            {previewData.scraperUsed && (
              <Badge variant="outline" className="ml-auto">
                {previewData.scraperUsed} Scraper
              </Badge>
            )}
          </div>

          {/* Select All */}
          <div className="flex items-center gap-2 px-1">
            <Checkbox
              checked={selectAll}
              onCheckedChange={handleSelectAll}
            />
            <label className="text-sm font-medium cursor-pointer" onClick={handleSelectAll}>
              Select All ({previewData.products.length} products)
            </label>
            <span className="ml-auto text-sm text-slate-600">
              {selectedProducts.size} selected
            </span>
          </div>

          {/* Products List */}
          <ScrollArea className="h-[400px] border rounded-lg">
            <div className="p-4 space-y-3">
              {previewData.products.map((product, index) => (
                <Card 
                  key={index} 
                  className={`cursor-pointer transition-colors ${
                    selectedProducts.has(index) ? 'border-blue-500 bg-blue-50/50' : ''
                  }`}
                  onClick={() => handleSelectProduct(index)}
                >
                  <CardContent className="p-4">
                    <div className="flex items-start gap-4">
                      <Checkbox
                        checked={selectedProducts.has(index)}
                        onCheckedChange={() => handleSelectProduct(index)}
                        onClick={(e) => e.stopPropagation()}
                      />

                      {/* Product Image */}
                      <div className="w-16 h-16 bg-slate-100 rounded flex-shrink-0">
                        {product.image ? (
                          <img 
                            src={product.image} 
                            alt={product.title}
                            className="w-full h-full object-contain rounded"
                          />
                        ) : (
                          <div className="w-full h-full flex items-center justify-center">
                            <Package className="h-6 w-6 text-slate-400" />
                          </div>
                        )}
                      </div>

                      {/* Product Details */}
                      <div className="flex-1 min-w-0">
                        <div className="flex items-start justify-between">
                          <div className="flex-1">
                            <h4 className="font-medium text-sm line-clamp-2">{product.title}</h4>
                            <div className="flex items-center gap-2 mt-1">
                              {product.modelNumber && product.modelNumber !== 'N/A' && (
                                <Badge variant="outline" className="text-xs">
                                  Model: {product.modelNumber}
                                </Badge>
                              )}
                              {product.brand && (
                                <Badge variant="outline" className="text-xs">
                                  {product.brand}
                                </Badge>
                              )}
                            </div>
                          </div>
                          <div className="text-right ml-4">
                            <div className="flex items-center gap-1">
                              <DollarSign className="h-3 w-3" />
                              <span className="font-semibold">{product.price?.toFixed(2) || 'N/A'}</span>
                            </div>
                          </div>
                        </div>

                        {/* Match Status */}
                        <div className="mt-2">
                          {product.isNew ? (
                            <div className="flex items-center gap-1 text-green-600">
                              <CheckCircle2 className="h-3 w-3" />
                              <span className="text-xs font-medium">New Product</span>
                            </div>
                          ) : product.matchedProduct ? (
                            <div className="flex items-center gap-1 text-blue-600">
                              <Link className="h-3 w-3" />
                              <span className="text-xs">
                                Matches: {product.matchedProduct.name} ({product.matchedProduct.modelNumber})
                              </span>
                            </div>
                          ) : null}
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          </ScrollArea>

          {/* Actions */}
          <div className="flex justify-between items-center pt-4 border-t">
            <div className="text-sm text-slate-600">
              {selectedProducts.size === 0 ? (
                "Select products to import"
              ) : (
                `${selectedProducts.size} product${selectedProducts.size === 1 ? '' : 's'} will be imported`
              )}
            </div>
            <div className="flex gap-2">
              <Button variant="outline" onClick={onClose} disabled={confirmImport.isPending}>
                Cancel
              </Button>
              <Button 
                onClick={handleConfirmImport}
                disabled={selectedProducts.size === 0 || confirmImport.isPending}
              >
                {confirmImport.isPending ? (
                  <>
                    <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                    Importing...
                  </>
                ) : (
                  <>
                    <ShoppingCart className="h-4 w-4 mr-2" />
                    Import Selected ({selectedProducts.size})
                  </>
                )}
              </Button>
            </div>
          </div>
        </div>
      </DialogContent>
    </Dialog>
  );
}
